fn main() {
    println!("max value of i32\t{}", std::i32::MAX);
    println!("max value of u32\t{}", std::u32::MAX);
    println!("max value of i64\t{}", std::i64::MAX);
    println!("max value of u64\t{}", std::u64::MAX);
    println!("max value of i128\t{}", std::i128::MAX);
    println!("max value of u128\t{}", std::u128::MAX);

    println!("max finite value of f32\t{}", std::f32::MAX);
    println!("max finite value of f64\t{}", std::f64::MAX);
}

